import bsv from"bsv";import fetch from"node-fetch";export const getKeys=function(a){const b=bsv.HDPrivateKey.fromString(a.xpriv),c=b.deriveChild("m/44'/0'/0'"),d=c.privateKey,e=c.publicKey,f=e.toAddress().toString();return{privateKey:d,publicKey:e,address:f}};export const getBalance=async function(a){const{address:b}=getKeys(a),c=await fetch("https://api.whatsonchain.com/v1/bsv/main/address/<address>/balance".replace("<address>",b)),d=await c.json();return d?.confirmed+d?.unconfirmed};export const getUtxos=async function(a){const{address:b}=getKeys(a),c=await fetch("https://api.whatsonchain.com/v1/bsv/main/address/<address>/unspent".replace("<address>",b));return c.json()};export const getTx=async function(a){const b=await fetch("https://api.whatsonchain.com/v1/bsv/main/tx/<hash>/hex".replace("<hash>",a));return b.text()};