import inquirer from"inquirer";import inquirerFileTreeSelection from"inquirer-file-tree-selection-prompt";inquirer.registerPrompt("file-tree-selection",inquirerFileTreeSelection);import os from"os";import path from"path";import chalk from"chalk";export const askAreYouSure=function(a){return inquirer.prompt([{name:"sure",type:"list",choices:["no","yes"],message:a||"Are you sure you want to delete this item?",validate:function(a){return!!a.length||"Please select an action"}}])};export const askWaitFunding=function(){return inquirer.prompt([{name:"selected",type:"list",choices:["try again","cancel"],message:"Try again when you have funded the address:",validate:function(a){return!!a.length||"Please select an action"}}])};export const askAction=function(){return inquirer.prompt([{name:"action",type:"list",choices:["post","reply","edit profile","resend id tx","delete profile","transfer sats","load sats","back","exit"],message:"Select action to perform:",validate:function(a){return!!a.length||"Please select an action"}}])};export const askMessage=function(){return inquirer.prompt([{name:"message",type:"input",message:"Post markdown text:",validate:function(a){return!!a.length||"Please write a message"}}])};export const askTxId=function(){return inquirer.prompt([{name:"txid",type:"input",message:"Transaction ID of original post:",validate:function(a){return!(64!==a.length)||"Please input a valid transaction ID"}}])};export const askAddress=function(){return inquirer.prompt([{name:"address",type:"input",message:"Bitcoin address to send funds to:",validate:function(a){return!!a.length||"Please fill in a Bitcoin address"}}])};export const askProfileName=function(a){return inquirer.prompt([{name:"name",type:"input",default:`ID ${a+1}`,message:"Profile name:",validate:function(a){return!!a.length||"Please input a name for this new profile"}}])};export const askProfile=function(a){const b=Object.keys(a.all),c=[{name:"profile",type:"list",choices:[...b,"+ import","+ new"],message:"Select profile:",validate:function(a){return!!a.length||"Please select a profile"}}];return inquirer.prompt(c)};export const askProfileImport=function(){const a=[{name:"file",type:"file-tree-selection",root:os.homedir(),message:"Select file to import:",validate:a=>{const b=a.split(path.sep).pop();return"."!==b[0]},onlyShowValid:!0,transformer(a){const b=a?a.split(path.sep).pop():"";return"."===b[0]?chalk.grey(b):b}}];return inquirer.prompt(a)};export const askProfileInfo=function(a={}){const b=[{name:"name",type:"input",default:a.name||"",message:"Enter your name:",validate:function(a){return!!a.length||"Please enter name"}},{name:"description",type:"input",default:a.description||a.bio||"",message:"Enter your bio / tagline:",validate:function(){return!0}},{name:"logo",type:"input",default:a.logo||"",message:"Avatar image url:",validate:function(){return!0}},{name:"banner",type:"input",default:a.banner||"",message:"Banner image url:",validate:function(){return!0}},{name:"location",type:"input",default:a.location||"",message:"Enter your location:",validate:function(){return!0}},{name:"url",type:"input",default:a.url||"",message:"Enter your website url:",validate:function(){return!0}},{name:"paymail",type:"input",default:a.paymail||"",message:"Enter your paymail address:",validate:function(){return!0}},{name:"bitcoinAddress",type:"input",default:a.bitcoinAddress||"",message:"Enter your bitcoin address:",validate:function(){return!0}}];return inquirer.prompt(b)};