import bsv from"bsv";import{BAP}from"bitcoin-bap";import{askProfileInfo,askProfileName}from"./inquirer.js";export const newProfile=async function(a){const b=Object.keys(a.all),c=await askProfileName(b.length),d=await askProfileInfo(),e=bsv.HDPrivateKey.fromRandom().toString(),f=new BAP(e),g=f.newId();g.name=c.name.replace(/[^a-z0-9_-]/ig,"_"),Object.keys(d).forEach(a=>{d[a]&&g.setAttribute(a,d[a])});const h=f.exportIds();a.set(g.name,{xpriv:e,ids:h})};