import qrcode from"qrcode-terminal";import{broadcastTransaction,getKeys}from"./bitcoin.js";import{askAddress}from"./inquirer.js";export const loadSats=function(a){const{address:b}=getKeys(a);qrcode.generate("bitcoin:"+b,function(a){console.log(a)}),console.log(`\nAddress: ${b}\n`)};export const transferSats=async function(a){const b=await askAddress();return broadcastTransaction(a,!1,b.address)};